import React, { useState } from 'react';

import './App.css';

function App() {

	interface trening {
		dataDayEvent: string,
		distantKmEvent: number,

	}

	const [arrayTreningDay, setArrayTreningDay] = useState<trening[]>([])
	const [dataDayEvent, setDataDay] = useState('')
	const [distantKmEvent, setDistantKm] = useState('')

	const diary = () => { // добовление даты и дистанции

		const parsDistant = parseFloat(distantKmEvent);

		if (dataDayEvent && !isNaN(parsDistant)) {

			const treningNewObj: trening = {
				dataDayEvent,
				distantKmEvent: parsDistant,
			};

			const index = arrayTreningDay.findIndex((trening) => new Date(trening.dataDayEvent) >= new Date(dataDayEvent))

			if (index !== -1) {
				const newArrayTreningDay = [...arrayTreningDay]
				treningNewObj.distantKmEvent += treningNewObj.distantKmEvent
				treningNewObj.distantKmEvent = treningNewObj.distantKmEvent.toFixed(1)
			}
		}
	}

	return (
		<div className='icont w-full'>
			<div className='input'>
				<input type="text" placeholder="Data:" className="input input-bordered w-full max-w-xs" />
				<input type="text" placeholder="Distance:(km)" className="input input-bordered w-full max-w-xs" />
				<button className="btn btn-outline btn-warning w-28">Ok</button>
			</div>

			<div className='table-link'>
				<table className='table'>
					<thead>
						<tr>
							<th><p>Дата</p></th>
							<th><p>Пройдено (км)</p></th>
							<th><p>Действия</p></th>
						</tr>
					</thead>
					<tbody>
						<tr>
							<td></td>
							<td></td>
							<td className='tdfree'>
								<button className='editbtn'></button>
								<button className="btn btn-square btn-outline btn-xs">
									<svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12" /></svg>
								</button>
							</td>
						</tr>
					</tbody>
				</table>
			</div>
		</div>

	);
}

export default App;
