import React, { useState } from 'react';

function App() {
	const [data, setData] = useState([]);
	const [date, setDate] = useState('');
	const [distance, setDistance] = useState('');

	const addData = () => {
		if (date && distance) {
			setData([...data, { date, distance }]);
			setDate('');
			setDistance('');
		}
	};

	return (
		<div className="p-8">
			<h1 className="text-2xl font-bold mb-4">Steps</h1>

			<div className="mb-4">
				<label>Date:</label>
				<input
					type="text"
					className="form-input"
					value={date}
					onChange={(e) => setDate(e.target.value)}
				/>
			</div>

			<div className="mb-4">
				<label>Distance (km):</label>
				<input
					type="number"
					className="form-input"
					value={distance}
					onChange={(e) => setDistance(e.target.value)}
				/>
			</div>

			<button className="btn btn-primary" onClick={addData}>
				Add Data
			</button>

			<div className="max-w-screen-lg mx-auto mt-4">
				<table className="table table-bordered">
					<thead>
						<tr>
							<th>Date</th>
							<th>Distance (km)</th>
						</tr>
					</thead>
					<tbody>
						{data.map((item, index) => (
							<tr key={index}>
								<td>{item.date}</td>
								<td>{item.distance}</td>
							</tr>
						))}
					</tbody>
				</table>
			</div>
		</div>
	);
}

export default App;
